import React, { useState } from "react";
import { Table, Menu, Dropdown, Button, Collapse } from "antd";
const { Panel } = Collapse;

const dataSource = [
  {
    key: "1",
    etapas: "Velocidade (m/s)",
    "1º Padrao": 97,
    "2º Padrao": 56,
    "3º Padrao": 32,
    tolerancia: 5,
  },
  {
    key: "2",
    etapas: "Velocidade (m/s)",
    "1º Padrao": 56,
    "2º Padrao": 64,
    "3º Padrao": 27,
    tolerancia: 3,
  },
  {
    key: "3",
    etapas: "Lim Pressao Trabalho",
    "1º Padrao": 48,
    "2º Padrao": 24,
    "3º Padrao": 32,
    tolerancia: 2,
  },
  {
    key: "4",
    etapas: "Lim Pressao Trabalho",
    "1º Padrao": 50,
    "2º Padrao": 16,
    "3º Padrao": 32,
    tolerancia: 4,
  },
];

export default function App() {
  const [paginationInfo, setPaginationInfo] = useState({
    current: 1,
    pageSize: 10,
  });

  const [columnList, setColumnList] = useState([
    {
      title: "etapas",
      dataIndex: "etapas",
      key: "etapas",
      fixed: "left",
      width: 150, // adicione essa propriedade para definir a largura da coluna
      render: (value, row, index) => {
        const trueIndex =
          index + paginationInfo.pageSize * (paginationInfo.current - 1);
        const obj = {
          children: value,
          props: {},
        };
        if (index >= 1 && value === dataSource[trueIndex - 1].etapas) {
          obj.props.rowSpan = 0;
        } else {
          for (
            let i = 0;
            trueIndex + i !== dataSource.length &&
            value === dataSource[trueIndex + i].etapas;
            i += 1
          ) {
            obj.props.rowSpan = i + 1;
          }
          if (columnList.some((col) => col.canDelete)) {
            // verifique se alguma coluna pode ser excluída
            obj.children = (
              <>
              <Button
type="link"
danger
onClick={() => {
setColumnList(
columnList.filter((col) => col.dataIndex !== "etapas")
);
}}
>
Delete
</Button>
{value}
</>
);
}
}
return obj;
},
},
{
title: "1º Padrao",
dataIndex: "1º Padrao",
key: "1º Padrao",
},
{
title: "2º Padrao",
dataIndex: "2º Padrao",
key: "2º Padrao",
},
{
title: "3º Padrao",
dataIndex: "3º Padrao",
key: "3º Padrao",
},
{
title: "Tolerância",
dataIndex: "tolerancia",
key: "tolerancia",
fixed: "right",
width: 100, // adicione essa propriedade para definir a largura da coluna
render: (value, row, index) => {
const trueIndex =
index + paginationInfo.pageSize * (paginationInfo.current - 1);
const obj = {
children: value,
props: {},
};
if (index >= 1 && value === dataSource[trueIndex - 1].tolerancia) {
obj.props.rowSpan = 0;
} else {
for (
let i = 0;
trueIndex + i !== dataSource.length &&
value === dataSource[trueIndex + i].tolerancia;
i += 1
) {
obj.props.rowSpan = i + 1;
}
if (columnList.some((col) => col.canDelete)) {
// verifique se alguma coluna pode ser excluída
obj.children = (
<>
<Button
type="link"
danger
onClick={() => {
setColumnList(
columnList.filter(
(col) => col.dataIndex !== "tolerancia"
)
);
}}
>
Delete
</Button>
{value}
</>
);
}
}
return obj;
},
},
]);

const handleMenuClick = (e) => {
const columnName = e.key;
setColumnList([
...columnList,
{
title: columnName,
dataIndex: columnName,
key: columnName,
canDelete: true, // adicione essa propriedade para indicar que essa coluna pode ser excluída
},
]);
};

const menu = (
<Menu onClick={handleMenuClick}>
{Object.keys(dataSource[0])
.filter((key) => key !== "key" && key !== "etapas" && key !== "tolerancia")
.map((key) => (
<Menu.Item key={key}>{key}</Menu.Item>
))}
</Menu>
);

return (
<>
<Collapse defaultActiveKey={["1"]}>
<Panel header="Customize Table" key="1">
<Dropdown overlay={menu}>
<Button>Add Column</Button>
</Dropdown>
</Panel>
</Collapse>
<Table
dataSource={dataSource}
columns={columnList}
pagination={{
current: paginationInfo.current,
pageSize: paginationInfo.pageSize,
onChange: (page, pageSize) => {
setPaginationInfo({ current: page, pageSize });
},
}}
scroll={{ x: "max-content" }}
/>
</>
);
}
