import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Table } from 'antd';

const Detalhes = () => {
  const { n_ParametroPadrao } = useParams();
  const [parametroPadrao, setParametroPadrao] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/renderizarpp/parametroPadraoDetalhado/${n_ParametroPadrao}`);
        setParametroPadrao(response.data);
        console.log(response.data);
      } catch (error) {
        console.error("Erro ao buscar detalhes do ParametroPadrao: ", error);
      }
    };
    fetchData();
  }, [n_ParametroPadrao]);

  let tabelas = {};
  let linhaAtual = {};
  if (parametroPadrao) {
    for (const resposta of parametroPadrao.TabelaRespostas) {
      const idTabela = resposta.TabelaColuna.n_TabelaGeral;
      if (!tabelas[idTabela]) {
        tabelas[idTabela] = {
          designacao: resposta.TabelaColuna.TabelaGeral.designacao,
          numero_linhas: resposta.TabelaColuna.TabelaGeral.numero_linhas,
          colunas: new Set(),
          linhas: {},
        };
      }
      tabelas[idTabela].colunas.add(resposta.TabelaColuna.TituloColunas);
  
      // calculando o número da linha
      const n_Linha =
  resposta.n_Linha !== undefined && resposta.n_Linha !== null // se n_Linha estiver definido
    ? resposta.n_Linha
    : (resposta.n_TabelaRespostas - 1) % tabela.numero_linhas + 1;
  
      if (!tabelas[idTabela].linhas[n_Linha]) {
        tabelas[idTabela].linhas[n_Linha] = { key: n_Linha };
      }
  
      tabela.linhas[n_Linha][resposta.TabelaColuna.TituloColunas] = resposta.respostas;
    }
  
    // Adicionar array das linhas para cada tabela
    for (const tabela of Object.values(tabelas)) {
      tabela.linhas = Object.values(tabela.linhas);
    }
  }
  
  return (
    <div>
      {parametroPadrao ? (
        <>
          Detalhes para o parametro padrao: {n_ParametroPadrao}
          <p>Descrição: {parametroPadrao.descricao}</p>
          <p>Estados:</p>
          {Object.entries(tabelas).map(([key, tabela]) => (
            <div key={key}>
              <h2>{tabela.designacao}</h2>
              <Table
                columns={Array.from(tabela.colunas).map((tituloColuna) => ({
                  title: tituloColuna,
                  dataIndex: tituloColuna,
                  key: tituloColuna,
                }))}
                dataSource={tabela.linhas}
                pagination={false}
              />
            </div>
          ))}
          <p>n_Formulario: {parametroPadrao.n_Formularios}</p>
          <p>n_VersaoFormulario: {parametroPadrao.n_VersaoFormulario}</p>
        </>
      ) : (
        <p>A carregar...</p>
      )}
    </div>
  );
};

export default Detalhes;
