import React, { useState, useEffect } from "react";
import axios from "axios";
import { Select, Table } from "antd";

const { Option } = Select;

function EscolherTabela() {
  const [tabelas, setTabelas] = useState([]);
  const [colunas, setColunas] = useState([]);
  const [renderTable, setRenderTable] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/escolhertabela/tabelas-geral")
      .then((res) => {
        setTabelas(res.data);
        if (res.data.length > 0) {
          handleTabelaChange(res.data[0].n_TabelaGeral);
        }
      })
      .catch((error) => {
        console.error("Erro ao obter tabelas", error);
      });
  }, []);

  const handleTabelaChange = (id) => {
    axios
      .get(`http://localhost:8080/api/escolhertabela/colunas/${id}`)
      .then((res) => {
        setColunas(res.data);
        setRenderTable(true);
      })
      .catch((error) => {
        console.error("Erro ao obter colunas", error);
      });
  };

  const columns = colunas?.map((coluna, index) => ({
    title: coluna.TituloColunas,
    dataIndex: `coluna${index}`,
    key: `coluna${index}`,
  }));

  // Exibição de dados vazios
  const dataSource = Array(5).fill({}).map((_, index) => {
    const row = {};
    colunas.forEach((_, i) => {
      row[`coluna${i}`] = "-";
    });
    row.key = index;
    return row;
  });

  return (
    <div>
      <h1>Escolha uma Tabela</h1>
      <Select style={{ width: 200 }} onChange={handleTabelaChange}>
        {tabelas.map((tabela) => (
          <Option key={tabela.n_TabelaGeral} value={tabela.n_TabelaGeral}>
            {tabela.designacao}
          </Option>
        ))}
      </Select>
      {renderTable && (
        <div>
          <h2>Tabela</h2>
          <Table columns={columns} dataSource={dataSource} pagination={false} />
        </div>
      )}
    </div>
  );
}

export default EscolherTabela;
