import React, { useState } from 'react';
import { Button, InputNumber, Form, Input, Table } from 'antd';

const { Item } = Form;

function Tabela() {
  const [numLinhas, setNumLinhas] = useState(0);
  const [numColunas, setNumColunas] = useState(0);
  const [dados, setDados] = useState([]);
  const [renderTable, setRenderTable] = useState(false);

  const handleNumLinhasChange = (value) => {
    setNumLinhas(value + 1);
    setDados((dados) => {
      const diferenca = value - dados.length + 1;
      if (diferenca > 0) {
        const novosDados = Array(diferenca)
          .fill(null)
          .map(() => Array(numColunas).fill(''));
        return [...dados, ...novosDados];
      } else {
        return dados.slice(0, value).concat([dados[dados.length - 1]]);
      }
    });
  };

  const handleNumColunasChange = (value) => {
    setNumColunas(value);
    setDados((dados) =>
      dados.map((linha) =>
        linha.length < value ? [...linha, ...Array(value - linha.length).fill('')] : linha.slice(0, value),
      ),
    );
  };

  const handleNomeColunaChange = (event, index) => {
    const { value } = event.target;
    setDados((dados) =>
      dados.map((linha, i) => {
        if (i === 0) {
          return linha.map((nome, j) => (j === index ? value : nome));
        } else {
          return linha;
        }
      }),
    );
  };

  const handleRenderTable = () => {
    setRenderTable(true);
  };

  const columns = dados[0]?.map((nomeColuna, index) => ({
    title: nomeColuna,
    dataIndex: `coluna${index}`,
    key: `coluna${index}`,
  }));

  const dataSource = dados.slice(1).map((linha, index) => {
    const row = {};
    linha.forEach((value, i) => {
      row[`coluna${i}`] = value;
    });
    row.key = index;
    return row;
  });

  return (
    <div>
      <Form layout="inline">
        <Item label="Linhas">
          <InputNumber min={0} value={Math.max(numLinhas - 1, 0)} onChange={handleNumLinhasChange} />
        </Item>
        <Item label="Colunas">
          <InputNumber min={0} value={numColunas} onChange={handleNumColunasChange} />
        </Item>
        {dados.length > 0 &&
          dados[0].map((nomeColuna, index) => (
            <Item key={index} label={`Coluna ${index + 1}`}>
              <Input value={nomeColuna} onChange={(event) => handleNomeColunaChange(event, index)} />
            </Item>
          ))}
        <Item>
          <Button type="primary" onClick={handleRenderTable}>
            Renderizar Tabela
          </Button>
        </Item>
      </Form>
      {renderTable && <Table columns={columns} dataSource={dataSource} pagination={false}/>}
    </div>
  );
}

export default Tabela;
