import React, { useState, useEffect } from "react";
import axios from "axios";
import { Select } from "antd";

const { Option } = Select;

function EscolherTabela() {
  const [tabelas, setTabelas] = useState([]);
  const [colunas, setColunas] = useState([]);

  useEffect(() => {
    // Quando o componente for montado, pegue todos os nomes das tabelas
    axios
      .get("http://localhost:8080/api/escolhertabela/tabelas-geral")
      .then((res) => {
        setTabelas(res.data);
      })
      .catch((error) => {
        console.error("Erro ao obter tabelas", error);
      });
  }, []);

  const handleTabelaChange = (nomeTabela) => {
    // Quando um nome de tabela for selecionado, pegue todas as colunas dessa tabela
    axios
      .get(`http://localhost:8080/api/escolhertabela/colunas/${nomeTabela}`)
      .then((res) => {
        setColunas(res.data);
      })
      .catch((error) => {
        console.error("Erro ao obter colunas", error);
      });
  };

  return (
    <div>
      <h1>Escolha uma Tabela</h1>
      <Select style={{ width: 200 }} onChange={handleTabelaChange}>
        {tabelas.map((tabela) => (
          <Option key={tabela} value={tabela}>
            {tabela}
          </Option>
        ))}
      </Select>
      {colunas.length > 0 && (
        <div>
          <h2>Colunas</h2>
          <ul>
            {colunas.map((coluna, index) => (
              <li key={index}>{coluna.TituloColunas}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default EscolherTabela;
