import React, { useState, useEffect } from "react";
import axios from "axios";
import { Select } from "antd";

const { Option } = Select;

function EscolherTabela() {
  const [tabelas, setTabelas] = useState([]);
  const [colunas, setColunas] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/escolhertabela/tabelasgeral")
      .then((res) => {
        setTabelas(res.data);
        if (res.data.length > 0) {
          handleTabelaChange(res.data[0].n_TabelaGeral);
        }
      })
      .catch((error) => {
        console.error("Erro ao obter tabelas", error);
      });
  }, []);

  const handleTabelaChange = (id) => {
    axios
      .get(`http://localhost:8080/api/escolhertabela/colunas/${id}`)
      .then((res) => {
        setColunas(res.data);
      })
      .catch((error) => {
        console.error("Erro ao obter colunas", error);
      });
  };

  return (
    <div>
      <h1>Escolha uma Tabela</h1>
      <Select style={{ width: 200 }} onChange={handleTabelaChange}>
        {tabelas.map((tabela) => (
          <Option key={tabela.n_TabelaGeral} value={tabela.n_TabelaGeral}>
            {tabela.designacao}
          </Option>
        ))}
      </Select>
      {colunas.length > 0 && (
        <div>
          <h2>Colunas</h2>
          <ul>
            {colunas.map((coluna, index) => (
              <li key={index}>{coluna.TituloColunas}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default EscolherTabela;
