import React, { useState } from "react";
import { Table, Menu, Dropdown, Button } from "antd";
import "antd/dist/antd.css";

const dataSource = [
  {
    key: "1",
    etapas: "Velocidade (m/s)",
    high: 97,
    middle: 56,
    low: 32,
    tolerancia: 5
  },
  {
    key: "2",
    etapas: "Velocidade (m/s)",
    high: 56,
    middle: 64,
    low: 27,
    tolerancia: 3
  },
  {
    key: "3",
    etapas: "Lim Pressao Trabalho",
    high: 48,
    middle: 24,
    low: 32,
    tolerancia: 2
  },
  {
    key: "4",
    etapas: "Lim Pressao Trabalho",
    high: 50,
    middle: 16,
    low: 32,
    tolerancia: 4
  }
];

export default function App() {
  const [paginationInfo, setPaginationInfo] = useState({
    current: 1,
    pageSize: 10
  });

  const [columnList, setColumnList] = useState([
    {
      title: "etapas",
      dataIndex: "etapas",
      key: "etapas",
      fixed: "left",
      render: (value, row, index) => {
        const trueIndex =
          index + paginationInfo.pageSize * (paginationInfo.current - 1);
        const obj = {
          children: value,
          props: {}
        };
        if (index >= 1 && value === dataSource[trueIndex - 1].etapas) {
          obj.props.rowSpan = 0;
        } else {
          for (
            let i = 0;
            trueIndex + i !== dataSource.length &&
            value === dataSource[trueIndex + i].etapas;
            i += 1
          ) {
            obj.props.rowSpan = i + 1;
          }
        }
        return obj;
      }
    },
    {
      title: "high",
      dataIndex: "high",
      key: "high"
    },
    {
      title: "middle",
      dataIndex: "middle",
      key: "middle"
    },
    {
      title: "low",
      dataIndex: "low",
      key: "low"
    },
    {
      title: "tolerancia",
      dataIndex: "tolerancia",
      key: "tolerancia",
      fixed: "right"
    }
  ]);

  const handleChange = (pagination) => {
    setPaginationInfo(pagination);
  };

  const handleAddColumn = ({ key }) => {
    const newColumnList = [...columnList];
    newColumnList.splice(key, 0, {
      title: `Column ${key}`,
      dataIndex: `column${key}`,
      key: `column${key}`,
    });
    setColumnList(newColumnList);
    };
    
    const menu = (
    <Menu onClick={handleAddColumn}>
    {columnList.map((column, index) => (
    <Menu.Item key={index}>Add Column {index + 1}</Menu.Item>
    ))}
    </Menu>
    );
    
    return (
    <div>
    <Dropdown overlay={menu}>
    <Button>Add Column</Button>
    </Dropdown>
    <Table
         dataSource={dataSource}
         columns={columnList}
         pagination={paginationInfo}
         onChange={handleChange}
         bordered
       />
    </div>
    );
    }
