import React, { useState } from 'react';
import { Button, InputNumber, Form, Input } from 'antd';
// import stringify from 'csv-stringify/lib/sync';
// import fs from 'fs';


const { Item } = Form;

function Tabela() {
  const [numLinhas, setNumLinhas] = useState(0);
  const [numColunas, setNumColunas] = useState(0);
  const [dados, setDados] = useState([]);

  const handleNumLinhasChange = (value) => {
    setNumLinhas(value);
    setDados((dados) => {
      const diferenca = value - dados.length;
      if (diferenca > 0) {
        const novosDados = Array(diferenca)
          .fill(null)
          .map(() => Array(numColunas).fill(''));
        return [...dados, ...novosDados];
      } else {
        return dados.slice(0, value);
      }
    });
  };

  const handleNumColunasChange = (value) => {
    setNumColunas(value);
    setDados((dados) =>
      dados.map((linha) =>
        linha.length < value ? [...linha, ...Array(value - linha.length).fill('')] : linha.slice(0, value),
      ),
    );
  };

  const handleNomeColunaChange = (event, index) => {
    const { value } = event.target;
    setDados((dados) =>
      dados.map((linha, i) => {
        if (i === 0) {
          return linha.map((nome, j) => (j === index ? value : nome));
        } else {
          return linha;
        }
      }),
    );
  };

//   const handleExportarCSV = () => {
//     const csv = stringify(dados, { header: true });
//     fs.writeFileSync('./data/tabela.csv', csv);
//   };

//   const handleExportarJSON = () => {
//     const json = JSON.stringify(dados);
//     fs.writeFileSync('./data/tabela.json', json);
//   };

  return (
    <div>
      <Form layout="inline">
        <Item label="Linhas">
          <InputNumber min={0} value={numLinhas} onChange={handleNumLinhasChange} />
        </Item>
        <Item label="Colunas">
          <InputNumber min={0} value={numColunas} onChange={handleNumColunasChange} />
        </Item>
        {dados.length > 0 &&
          dados[0].map((nomeColuna, index) => (
            <Item key={index} label={`Coluna ${index + 1}`}>
              <Input value={nomeColuna} onChange={(event) => handleNomeColunaChange(event, index)} />
            </Item>
          ))}
      </Form>
      <Button onClick={handleExportarCSV}>Exportar CSV</Button>
      <Button onClick={handleExportarJSON}>Exportar JSON</Button>
    </div>
  );
}

export default Tabela;
