import { SearchOutlined } from '@ant-design/icons';
import { Button, Input, Space, Table, Modal, Form, Descriptions  } from 'antd';
import { useEffect, useRef, useState } from 'react';
import Highlighter from 'react-highlight-words';
import MaquinaSwitch from "./switch.jsx";
import axios from 'axios';

const App = () => {
  const [searchText, setSearchText] = useState('');
  const [searchedColumn, setSearchedColumn] = useState('');
  const [data, setData] = useState([]);
  const searchInput = useRef(null);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [form] = Form.useForm();
  const [newMachine, setNewMachine] = useState({});
  const [detailsModalVisible, setDetailsModalVisible] = useState(false);
  const [machineDetails, setMachineDetails] = useState({});

    
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios('http://localhost:8080/api/maquinas/listar');
      // Adicionando uma propriedade 'key' a cada item.
      const dataWithKeys = result.data.map(item => ({...item, key: item.n_Maquina}));
      setData(dataWithKeys);
    };
    fetchData();
  }, []);

  const fetchMachineDetails = async (id) => {
    const result = await axios(`http://localhost:8080/api/maquinas/listar/${id}`);
    return result.data;
  };

  const handleMachineClick = async (id) => {
    const machineDetails = await fetchMachineDetails(id);
    setMachineDetails(machineDetails);
    setDetailsModalVisible(true);
  };

  const handleOk = async () => {
    try {
      const values = await form.validateFields();
      values.EstadoMaquina = 1;
      axios.post('http://localhost:8080/api/maquinas/adicionar', values)
      .then(function (response) {
        console.log(response);
        setIsModalVisible(false);
        form.resetFields();
        // atualize a tabela aqui
      })
      .catch(function (error) {
        console.log(error);
      });
    } catch (errorInfo) {
      console.log('Failed:', errorInfo);
    }
  };
  
  
  const handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    setSearchText(selectedKeys[0]);
    setSearchedColumn(dataIndex);
  };
  
  const handleReset = (clearFilters) => {
    clearFilters();
    setSearchText('');
  };
  const getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters, close }) => (
      <div
        style={{
          padding: 8,
        }}
        onKeyDown={(e) => e.stopPropagation()}
      >
        <Input
          ref={searchInput}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={(e) => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => handleSearch(selectedKeys, confirm, dataIndex)}
          style={{
            marginBottom: 8,
            display: 'block',
          }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{
              width: 90,
            }}
          >
            Search
          </Button>
          <Button
            onClick={() => clearFilters && handleReset(clearFilters)}
            size="small"
            style={{
              width: 90,
            }}
          >
            Reset
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              confirm({
                closeDropdown: false,
              });
              setSearchText(selectedKeys[0]);
              setSearchedColumn(dataIndex);
            }}
          >
            Filter
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              close();
            }}
          >
            close
          </Button>
        </Space>
      </div>
    ),
    filterIcon: (filtered) => (
      <SearchOutlined
        style={{
          color: filtered ? '#1890ff' : undefined,
        }}
      />
    ),
    onFilter: (value, record) =>
      record[dataIndex].toString().toLowerCase().includes(value.toLowerCase()),
    onFilterDropdownOpenChange: (visible) => {
      if (visible) {
        setTimeout(() => searchInput.current?.select(), 100);
      }
    },
    render: (text) =>
      searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{
            backgroundColor: '#ffc069',
            padding: 0,
          }}
          searchWords={[searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ''}
        />
      ) : (
        text
      ),
  });
  const columns = [
    {
      title: 'ID',
      dataIndex: 'n_Maquina',
      key: 'n_Maquina',
      width: '30%',
      ...getColumnSearchProps('n_Maquina'),
      render: (text, record) => <a onClick={() => handleMachineClick(record.n_Maquina)}>{text}</a>,
    },
    {
      title: 'Marca/Modelo',
      dataIndex: 'MarcaModelo',
      key: 'MarcaModelo',
      width: '50%',
      ...getColumnSearchProps('MarcaModelo'),
      render: (text, record) => <a onClick={() => handleMachineClick(record.n_Maquina)}>{text}</a>,
    },
    {
      title: 'Estado',
      dataIndex: 'EstadoMaquina',
      key: 'EstadoMaquina',
      render: (estado) => <MaquinaSwitch estado={estado} />,
    }
  ];
  return (
    <>
      <Button type="primary" onClick={() => setIsModalVisible(true)}>
        Adicionar Máquina
      </Button>
      <Modal
        title="Adicionar Máquina"
        visible={isModalVisible}
        onCancel={() => setIsModalVisible(false)}
        footer={[
          <Button key="back" onClick={() => setIsModalVisible(false)}>
            Cancelar
          </Button>,
          <Button key="submit" type="primary" onClick={handleOk}>
            Adicionar
          </Button>,
        ]}
      >
        <Form form={form} layout="vertical">
          <Form.Item
            name="MarcaModelo"
            label="Marca/Modelo"
            rules={[
              {
                required: true,
                message: "Por favor insira a marca/modelo da máquina",
              },
            ]}
          >
            <Input
              onChange={(e) =>
                setNewMachine({ ...newMachine, MarcaModelo: e.target.value })
              }
            />
          </Form.Item>
          <Form.Item
            name="ForçaFecho"
            label="Força de Fecho"
            rules={[
              {
                required: true,
                message: "Por favor insira a força de fecho",
              },
            ]}
          >
            <Input
              onChange={(e) =>
                setNewMachine({ ...newMachine, ForcaFecho: e.target.value })
              }
            />
          </Form.Item>
          <Form.Item
            name="DiametroFuso"
            label="Diâmetro do Fuso"
            rules={[
              {
                required: true,
                message: "Por favor insira o diâmetro do fuso",
              },
            ]}
          >
            <Input
              onChange={(e) =>
                setNewMachine({ ...newMachine, DiametroFuso: e.target.value })
              }
            />
          </Form.Item>
        </Form>
      </Modal>
      <Modal
        title="Detalhes da Máquina"
        visible={detailsModalVisible}
        onOk={() => setDetailsModalVisible(false)}
        onCancel={() => setDetailsModalVisible(false)}
        footer={null}
        maskClosable={true}
      >
        <Descriptions column={1}>
          <Descriptions.Item label="ID">
            {machineDetails.n_Maquina}
          </Descriptions.Item>
          <Descriptions.Item label="Marca/Modelo">
            {machineDetails.MarcaModelo}
          </Descriptions.Item>
          <Descriptions.Item label="Força de Fecho">
            {machineDetails.ForcaFecho}
          </Descriptions.Item>
          <Descriptions.Item label="Diâmetro do Fuso">
            {machineDetails.DiametroFuso}
          </Descriptions.Item>
          {/* Adicione aqui outros campos conforme necessário */}
        </Descriptions>
      </Modal>
      <Table columns={columns} dataSource={data} />
    </>
  );
};

export default App;
