import { Table, Button, Collapse } from 'antd';
import { useState } from 'react';

const { Panel } = Collapse;
const { Column } = Table;

const initialColumns = [
  {
    title: 'Unidades',
    dataIndex: 'c',
    key: 'c',
  },
  {
    title: '1-Bico',
    dataIndex: 'bico1',
    key: 'bico1',
  },
  {
    title: 'Tolerancia. Max(+/-)',
    dataIndex: 'tolerancia',
    key: 'tolerancia',
    fixed: 'right',
  },
];

const initialData = [
  {
    key: 'Padrao',
    c: 'ºC',
    bico1: '295',
    tolerancia: '10',
  },
];

const App = () => {
  const [columns, setColumns] = useState(initialColumns);

  const handleAddColumns = () => {
    const newColumns = [
      ...columns,
      { title: '5-Cil', dataIndex: 'cil5', key: 'cil5' },
      { title: '6-Cil', dataIndex: 'cil6', key: 'cil6' },
      { title: '7-Cil', dataIndex: 'cil7', key: 'cil7' },
    ];
    setColumns(newColumns);
  };

  const handleRemoveColumns = () => {
    const newColumns = columns.slice(0, -3);
    setColumns(newColumns);
  };

  return (
    <Collapse size="small" defaultActiveKey={['1']}>
      <Panel header="Temperatura das Resistências do Fuso" key="1">
        <Button onClick={handleAddColumns}>Adicionar Colunas</Button>
        <Button onClick={handleRemoveColumns}>Remover Colunas</Button>
        <Table dataSource={initialData} pagination={false} scroll={{ x: 'max-content' }}>
          {columns.map((column) => (
            <Column {...column} />
          ))}
        </Table>
      </Panel>
    </Collapse>
  );
};

export default App;
