import { Table, Button, Collapse } from "antd";
import { useState } from 'react';

const { Panel } = Collapse;
const { Column } = Table;

const defaultData = [
  {
    key: "Padrao",
    c: "ºC",
    bico1: "295",
    cil2: "290",
    cil3: "285",
    cil4: "280",
    tolerancia: "10",
  },
];

const defaultColumns = [
  "key",
  "c",
  "bico1",
  "cil2",
  "cil3",
  "cil4",
  "tolerancia",
];

const App = () => {
  const [data, setData] = useState(defaultData);
  const [columns, setColumns] = useState(defaultColumns);

  const addColumn = (key) => {
    setColumns([...columns.slice(0, -1), key, "tolerancia"]);
  };

  const removeColumn = (key) => {
    setColumns(columns.filter((column) => column !== key));
  };

  return (
    <Collapse size="small" defaultActiveKey={['1']}>
      <Panel header="Temperatura das Resistências do Fuso" key="1">
        <Button onClick={() => addColumn("cil5")}>Adicionar 5-Cil</Button>
        <Button onClick={() => addColumn("cil6")}>Adicionar 6-Cil</Button>
        <Button onClick={() => addColumn("cil7")}>Adicionar 7-Cil</Button>
        <Button onClick={() => removeColumn("cil5")}>Remover 5-Cil</Button>
        <Button onClick={() => removeColumn("cil6")}>Remover 6-Cil</Button>
        <Button onClick={() => removeColumn("cil7")}>Remover 7-Cil</Button>
        <Table dataSource={data} pagination={false}>
          {columns.map((column) => (
            <Column title={column} dataIndex={column} key={column} />
          ))}
        </Table>
      </Panel>
    </Collapse>
  );
};

export default App;
