import React from 'react';
import 'bootstrap-icons/font/bootstrap-icons.css';



const LogoutButton = ({ onLogout }) => {
    const handleLogoutClick = () => {
        // Limpar o localStorage
        localStorage.removeItem("token");
        localStorage.removeItem("userPhoto");
        localStorage.removeItem("username");
        
        // Executar qualquer outra lógica passada como prop (por exemplo, redirecionar para a página de login)
        onLogout();
    };

    return (
        <button
        type="button"
        onClick={handleLogoutClick}
        class="text-white bg-[#24292F] hover:bg-[#24292F]/90 focus:outline-none font-medium text-sm px-5 py-2.5 text-center inline-flex items-center dark:focus:ring-gray-500 dark:hover:bg-[#050708]/30 mr-2 mb-2"
    >
        Logout
        <i className="bi bi-box-arrow-left ml-5"></i>  {/* Adicionei "ml-2" para dar um pequeno espaço entre o texto e o ícone */}
    </button>
    )    
};

export default LogoutButton;
