import React, { useState } from "react";
import { Table } from "antd";
import "antd/dist/antd.css";

const dataSource = [
  {
    key: "1",
    etapas: "Velocidade (m/s)",
    high: 97,
    middle: 56,
    low: 32
  },
  {
    key: "2",
    etapas: "Velocidade (m/s)",
    high: 56,
    middle: 64,
    low: 27
  },
  {
    key: "3",
    etapas: "Lim Pressao Trabalho",
    high: 48,
    middle: 24,
    low: 32
  },
  {
    key: "4",
    etapas: "Lim Pressao Trabalho",
    high: 50,
    middle: 16,
    low: 32
  }
];

export default function App() {
  const [paginationInfo, setPaginationInfo] = useState({
    current: 1,
    pageSize: 10
  });

  const columns = [
    {
      title: "etapas",
      dataIndex: "etapas",
      key: "etapas",
      render: (value, row, index) => {
        const trueIndex =
          index + paginationInfo.pageSize * (paginationInfo.current - 1);
        const obj = {
          children: value,
          props: {}
        };
        if (index >= 1 && value === dataSource[trueIndex - 1].etapas) {
          obj.props.rowSpan = 0;
        } else {
          for (
            let i = 0;
            trueIndex + i !== dataSource.length &&
            value === dataSource[trueIndex + i].etapas;
            i += 1
          ) {
            obj.props.rowSpan = i + 1;
          }
        }
        return obj;
      }
    },
    {
      title: "high",
      dataIndex: "high",
      key: "high"
    },
    {
      title: "middle",
      dataIndex: "middle",
      key: "middle"
    },
    {
      title: "low",
      dataIndex: "low",
      key: "low"
    }
  ];

  const handleChange = (pagination) => {
    setPaginationInfo(pagination);
  };
  return (
    <div className="App">
      <Table
        onChange={handleChange}
        dataSource={dataSource}
        columns={columns}
        bordered
      />
    </div>
  );
}
