import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Form, Input, Button, Checkbox, Alert } from 'antd';
import axios from 'axios';
import jwt_decode from 'jsonwebtoken';
import { isNotExpiredBoolean } from './token'; // Certifique-se de que o caminho para token.js está correto

const axiosInstance = axios.create({
  baseURL: 'http://localhost:8080', // Adicione a URL completa do backend aqui
});

const LoginComponent = () => {
  const [error, setError] = useState('');
  const history = useHistory();
  const [username, setusername] = useState("");
  const [password, setpassword] = useState("");

  if (isNotExpiredBoolean()) {
    // Se o token for válido, redirecionar para a página de dashboard
    history.push('/');
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axiosInstance.post('/login', { username, password });
      if (response.status === 200) {
        const data = response.data;
        
        // Verificar se o token é válido antes de armazená-lo
        const decodedToken = verifyToken(data.token, 'segredo_jwt');
        if (decodedToken) {
          localStorage.setItem("token", data.token);
          history.push('/');
        } else {
          setError('Invalid token');
        }
      }
    } catch (err) {
      setError('Authentication failed');
    }
  };


  return (
    <div className="login-container">
      {error && <Alert message={error} type="error" showIcon />}
      <form onSubmit={handleSubmit}>
                <div className="input-boxes">
                  
                  <div className="mb-6">
                  <label htmlFor="email-address">Endereço e-mail</label>
                      <input value={username} onChange={(value) => setusername(value.target.value)} type="text" id="email" className="mt-1 appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm" placeholder="nome@softinsa.pt" required />
                  </div>
                  <div className="mb-6">
                  <label htmlFor="email-address">Palavra-passe</label>
                      <input value={password} onChange={(value) => setpassword(value.target.value)} type="password" id="password" className="mt-1 appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm" placeholder="Palavra-passe" required />
                  </div>
                  <div className="button input-box">
                    <input type="submit" value="Entrar" />
                  </div>
                  <div className="text sign-up-text"><a onClick={()=>forgotPassword()}>Esqueceu-se da palavra-passe?</a></div>
                </div>
            </form>
    </div>
  );
};

export default LoginComponent;
