import React, { useState, useEffect } from "react";
import { Button, InputNumber, Form, Input, Table, Checkbox  } from "antd";
import axios from "axios";

const { Item } = Form;

function Tabela() {
  const [numLinhas, setNumLinhas] = useState(0);
  const [numColunas, setNumColunas] = useState(0);
  const [dados, setDados] = useState([]);
  const [renderTable, setRenderTable] = useState(false);
  const [nomeTabela, setNomeTabela] = useState("");
  const [Real, setReal] = useState(Array(numColunas).fill(0));

  useEffect(() => {
    setReal(Array(numColunas).fill(0));
  }, [numColunas]);


  const handleNumLinhasChange = (value) => {
    setNumLinhas(value);
    setDados((dados) => {
      const diferenca = value - (dados.length - 1);  // subtrai 1 da contagem de linhas de dados
      if (diferenca > 0) {
        const novosDados = Array(diferenca)
          .fill(null)
          .map(() => Array(numColunas).fill(""));
        return [...dados, ...novosDados];
      } else {
        return [dados[0], ...dados.slice(1, value + 1)];  // adiciona 1 ao índice final
      }
    });
  };


  const handleNumColunasChange = (value) => {
    setNumColunas(value);
    setDados((dados) =>
      dados.map((linha) =>
        linha.length < value
          ? [...linha, ...Array(value - linha.length).fill("")]
          : linha.slice(0, value)
      )
    );
  };

  const handleNomeColunaChange = (event, index) => {
    const { value } = event.target;
    setDados((dados) =>
      dados.map((linha, i) => {
        if (i === 0) {
          return linha.map((nome, j) => (j === index ? value : nome));
        } else {
          return linha;
        }
      })
    );
  };

  const handleRealCheckboxChange = (event, index) => {
    const { checked } = event.target;
    setReal((Real) => {
      const newReal = [...Real];
      newReal[index] = checked ? "Sim" : "Não";
      return newReal;
    });
  };

  // Inicialize o array Real com 0 para cada coluna
const initialRealValues = Array(numColunas).fill(0);

const handleFormSubmit = async (event) => {
  event.preventDefault();
  try {
    console.log("Dados da tabela:");
    console.log("Nome da Tabela:", nomeTabela);
    console.log("Número de Linhas:", numLinhas);
    console.log("Número de Colunas:", numColunas);
    console.log("Dados das Colunas:", dados[0]);
    console.log("Valores Real:", Real);

    // Criação da tabela geral
    const resGeral = await axios.post(
      "http://localhost:8080/api/tabela/geral",
      {
        nome: nomeTabela,
        numero_linhas: numLinhas,
      }
    );

    console.log("Valores colunas:", dados[0]);
    console.log("Valores Real2:", Real);

    // Preencha o array Real com os valores corretos
    const realValues = initialRealValues.map((value, index) => {
      return Real[index] !== undefined ? Real[index] : value;
    });

    // Criação das colunas
    const colunasRequests = dados[0].map(async (tituloColuna, index) => {
      return await axios.post("http://localhost:8080/api/tabela/colunas", {
        n_TabelaGeral: resGeral.data.n_TabelaGeral,
        colunas: [tituloColuna],
        Real: realValues[index],
      });
    });
    await Promise.all(colunasRequests);

    console.log("Tabela e colunas criadas com sucesso");
    setRenderTable(true);

    // setNumLinhas(0);
    // setNumColunas(0);
    // setDados([]);
    // setNomeTabela("");
  } catch (error) {
    console.error("Erro ao criar tabela e colunas", error);
  }
};


  const columns = dados[0]?.map((nomeColuna, index) => ({
    title: nomeColuna,
    dataIndex: `coluna${index}`,
    key: `coluna${index}`,
  }));

  const dataSource = dados.slice(1).map((linha, index) => {
    const row = {};
    linha.forEach((value, i) => {
      row[`coluna${i}`] = value;
    });
    row.key = index;
    return row;
  });

  return (
    <div>
      <Form layout="inline" onSubmit={handleFormSubmit}>
        <Form.Item label="Linhas">
          <InputNumber
            min={0}
            value={numLinhas}
            onChange={handleNumLinhasChange}
          />
        </Form.Item>
        <Form.Item label="Colunas">
          <InputNumber
            min={0}
            value={numColunas}
            onChange={handleNumColunasChange}
          />
        </Form.Item>
        <Form.Item label="Nome da Tabela">
          <Input
            value={nomeTabela}
            onChange={(e) => setNomeTabela(e.target.value)}
          />
        </Form.Item>
        <Form.Item>
          <Button type="primary" onClick={handleFormSubmit}>
            Criar Tabela
          </Button>
        </Form.Item>
      </Form>
  
      {dados[0]?.map((_, index) => (
  <div key={index}>
    <Input
      placeholder={`Nome da coluna ${index + 1}`}
      onChange={(event) => handleNomeColunaChange(event, index)}
    />
    <label htmlFor={`real-checkbox-${index}`}>
      Real:
      <Checkbox
        id={`real-checkbox-${index}`}
        checked={Real[index] === "Sim"}
        onChange={(event) => handleRealCheckboxChange(event, index)}
      >
        {Real[index] === "Sim" ? "Sim" : "Não"}
      </Checkbox>
    </label>
  </div>
))}






    </div>
  );
      }
  

export default Tabela;
