import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Form, Input, Button, Checkbox, Alert } from 'antd';
import axios from 'axios';
import jwt_decode from 'jsonwebtoken';
import {isNotExpired} from './token'

const axiosInstance = axios.create({
  baseURL: 'http://localhost:8080', // Adicione a URL completa do backend aqui
});

const LoginComponent = () => {
  const [error, setError] = useState('');
  const history = useHistory();

  //isNotExpired();

//   const verifyTokenExpiration = (token) => {
//     try {
//       const decodedToken = jwt_decode(token);
//       const currentTime = Date.now() / 1000; // Obter o tempo atual em segundos

//       if (decodedToken.exp < currentTime) {
//         // O token expirou, remover do localStorage
//         localStorage.removeItem('token');
//         return false;
//       }

//       return true;
//     } catch (err) {
//       // Se houver algum erro ao decodificar o token, considere-o inválido
//       localStorage.removeItem('token');
//       return false;
//     }
//   };

  // Verificar se o token está presente e é válido ao carregar a página
  React.useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      const isTokenValid = verifyTokenExpiration(token);

      if (!isTokenValid) {
        setError('Token has expired');
      }
    }
  }, []);

  const onFinish = async (values) => {
    try {
      // Fazer a chamada para o backend de autenticação usando o Axios
      const response = await axiosInstance.post('/login', values);

      if (response.status === 200) {
        const data = response.data;
        localStorage.setItem('token', data.token); // Armazenar o token no localStorage
        history.push('/'); // Redirecionar para a página de dashboard após o login
      } else {
        setError('Authentication failed');
      }
    } catch (err) {
      setError('Error occurred during authentication');
    }
  };

  return (
    <div className="login-container">
      {error && <Alert message={error} type="error" showIcon />}
      <Form name="login" onFinish={onFinish}>
        <Form.Item
          label="Username"
          name="username"
          rules={[{ required: true, message: 'Please input your username!' }]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label="Password"
          name="password"
          rules={[{ required: true, message: 'Please input your password!' }]}
        >
          <Input.Password />
        </Form.Item>
        <Form.Item name="remember" valuePropName="checked">
          <Checkbox>Remember me</Checkbox>
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Login
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default LoginComponent;
