import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Table, Button } from 'antd';

const Detalhes = () => {
  const { n_ParametroPadrao } = useParams();
  const [parametroPadrao, setParametroPadrao] = useState(null);
  const [n_Estados, setNEstados] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/renderizarpp/parametroPadraoDetalhado/${n_ParametroPadrao}`);
        setParametroPadrao(response.data);
      } catch (error) {
        console.error("Erro ao buscar detalhes do ParametroPadrao: ", error);
      }
    };
    fetchData();
  }, [n_ParametroPadrao]);

  useEffect(() => {
    const fetchNEstados = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/renderizarpp/parametroPadrao/${n_ParametroPadrao}`);
        setNEstados(response.data.n_Estados);
      } catch (error) {
        console.error("Erro ao buscar n_Estados: ", error);
      }
    };
    fetchNEstados();
  }, [n_ParametroPadrao]);

  let tabelas = {};

  if (parametroPadrao) {
    for (const resposta of parametroPadrao.TabelaRespostas) {
      const idTabela = resposta.TabelaColuna.n_TabelaGeral;
      
      if (!tabelas[idTabela]) {
        tabelas[idTabela] = {
          designacao: resposta.TabelaColuna.TabelaGeral.designacao,
          numero_linhas: resposta.TabelaColuna.TabelaGeral.numero_linhas,
          colunas: new Set(),
          linhas: [],
        };
      }

      tabelas[idTabela].colunas.add(resposta.TabelaColuna.TituloColunas);

      // Assume que o número da linha está incluído no início da resposta
      const linha = parseInt(resposta.respostas.match(/linha (\d+)/)[1], 10);

      if (!tabelas[idTabela].linhas[linha - 1]) {
        tabelas[idTabela].linhas[linha - 1] = { key: linha };
      }

      tabelas[idTabela].linhas[linha - 1][resposta.TabelaColuna.TituloColunas] = resposta.respostas;
    }
  }

  console.log(tabelas);

  const renderButtons = (n_Estados) => {
    switch (n_Estados) {
      case 3:
        return (
          <>
            <Button type="primary">Submited</Button>
            <Button>Cancel</Button>
          </>
        );
      case 4:
        return (
          <>
            <Button type="primary">Deviated</Button>
            <Button>Activated</Button>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <div>
      {parametroPadrao ? (
        <>
          Detalhes para o parametro padrao: {n_ParametroPadrao}
          <p>Descrição: {parametroPadrao.descricao}</p>
          <p>Estados:</p>
          {Object.entries(tabelas).map(([key, tabela]) => (
            <div key={key}>
              <h2>{tabela.designacao}</h2>
              <Table
                columns={Array.from(tabela.colunas).map((coluna) => ({
                  title: coluna,
                  dataIndex: coluna,
                  key: coluna,
                }))}
                dataSource={tabela.linhas}
                pagination={false}
              />
            </div>
          ))}
          <p>n_Formulario: {parametroPadrao.n_Formularios}</p>
          <p>n_VersaoFormulario: {parametroPadrao.n_VersaoFormularios}</p>
          {renderButtons(n_Estados)}
        </>
      ) : (
        <p>A carregar...</p>
      )}
    </div>
  );
};

export default Detalhes;
