import React, { useState } from "react";
import { Table, Menu, Dropdown, Button } from "antd";

const dataSource = [
  {
    key: "1",
    etapas: "Velocidade (m/s)",
    "1º Padrao": 97,
    "2º Padrao": 56,
    "3º Padrao": 32,
    tolerancia: 5,
  },
  {
    key: "2",
    etapas: "Velocidade (m/s)",
    "1º Padrao": 56,
    "2º Padrao": 64,
    "3º Padrao": 27,
    tolerancia: 3,
  },
  {
    key: "3",
    etapas: "Lim Pressao Trabalho",
    "1º Padrao": 48,
    "2º Padrao": 24,
    "3º Padrao": 32,
    tolerancia: 2,
  },
  {
    key: "4",
    etapas: "Lim Pressao Trabalho",
    "1º Padrao": 50,
    "2º Padrao": 16,
    "3º Padrao": 32,
    tolerancia: 4,
  },
];

export default function App() {
  const [paginationInfo, setPaginationInfo] = useState({
    current: 1,
    pageSize: 10,
  });

  const [columnList, setColumnList] = useState([
    {
      title: "etapas",
      dataIndex: "etapas",
      key: "etapas",
      fixed: "left",
      render: (value, row, index) => {
        const trueIndex =
          index + paginationInfo.pageSize * (paginationInfo.current - 1);
        const obj = {
          children: value,
          props: {},
        };
        if (index >= 1 && value === dataSource[trueIndex - 1].etapas) {
          obj.props.rowSpan = 0;
        } else {
          for (
            let i = 0;
            trueIndex + i !== dataSource.length &&
            value === dataSource[trueIndex + i].etapas;
            i += 1
          ) {
            obj.props.rowSpan = i + 1;
          }
        }
        return obj;
      },
    },
    {
      title: "1º Padrao",
      dataIndex: "1º Padrao",
      key: "1º Padrao",
    },
    {
      title: "2º Padrao",
      dataIndex: "2º Padrao",
      key: "2º Padrao",
    },
    {
      title: "3º Padrao",
      dataIndex: "3º Padrao",
      key: "3º Padrao",
    },
    {
      title: "tolerancia",
      dataIndex: "tolerancia",
      key: "tolerancia",
      fixed: "right",
    },
  ]);

  const handleAddColumn = () => {
    const newColumn = {
      title: `${columnList.length - 2 + 1}º Padrao`,
      dataIndex: `${columnList.length - 2 + 1}º Padrao`,
      key: `${columnList.length - 2 + 1}`,
    };
    setColumnList([
      ...columnList.slice(0, -1),
      newColumn,
      columnList.slice(-1)[0],
    ]);
  };

  const menu = (
    <Menu>
      <Menu.Item>
        <Button onClick={handleAddColumn}>Add Column</Button>
      </Menu.Item>
    </Menu>
  );

  return (
    
    <div style={{ padding: 16 }}>
      <Button
        onClick={handleAddColumn}
        overlay={menu}
        placement="bottomLeft"
        trigger={["click"]}
      >
        {" "}
        Add Column{" "}
      </Button>
      <Table
        dataSource={dataSource}
        columns={columnList}
        pagination={{
          pageSize: paginationInfo.pageSize,
          current: paginationInfo.current,
          onChange: (page, pageSize) =>
            setPaginationInfo({ ...paginationInfo, current: page }),
        }}
        scroll={{ x: "max-content" }}
        bordered
      />
    </div>
  );
}
