    },
    {
        id: "Moldes",
        title: "Moldes",
        navLink: "/pages/Moldes",
    },
];



function determinePagesBasedOnRole() {
    const token = localStorage.getItem('token');
    if (!token) return [];

    const decodedJwt = parseJwt(token);
    if (!decodedJwt || !decodedJwt.departmentNumber) return [];

    switch (decodedJwt.departmentNumber) {
        case 'IT':
            return ITPagesRoles;
        case 'Quality':
            return QualityPagesRoles;
        case 'Collaborator':
            return CollaboratorPagesRoles;
        default:
            return [];
    }
}

// Esta constante pode ser importada em outros componentes para obter as páginas do role atual
export const pagesForCurrentUser = determinePagesBasedOnRole();

// Exportando as Roles definidas para que possam ser usadas em outros lugares se necessário
export default {
    ITPagesRoles,
    QualityPagesRoles,
    CollaboratorPagesRoles,
}
