import React, { useState, useEffect } from "react";
import axios from "axios";
import { Select, Table, Button } from "antd";

const { Option } = Select;

function RenderizarFormulario() {
  const [formularios, setFormularios] = useState([]);
  const [tabelas, setTabelas] = useState([]);
  const [selectedFormulario, setSelectedFormulario] = useState(null);
  const [tabelasFormulario, setTabelasFormulario] = useState([]);
  const [selectedTable, setSelectedTable] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/editarformulario/formularios")
      .then((res) => {
        console.log('Resposta da API (Formulários):', res.data);
        setFormularios(res.data);
      })
      .catch((error) => {
        console.error("Erro ao obter formulários", error);
      });
      
    axios
      .get("http://localhost:8080/api/escolhertabela/tabelas-geral")
      .then((res) => {
        setTabelas(res.data);
      })
      .catch((error) => {
        console.error("Erro ao obter tabelas", error);
      });

  }, []);

  const handleFormularioChange = (value) => {
    setSelectedFormulario(value);
    axios
      .get(`http://localhost:8080/api/editarformulario/formularios/${value}`)
      .then((res) => {
        console.log('Resposta da API (Tabelas do Formulário):', res.data);
        setTabelasFormulario(res.data);
      })
      .catch((error) => {
        console.error("Erro ao obter tabelas do formulário", error);
      });
  };

  const handleTableChange = (value) => {
    setSelectedTable(value);
  };

  const handleAddTable = () => {
    if(selectedTable){
      axios
        .get(`http://localhost:8080/api/escolhertabela/colunas/${selectedTable}`)
        .then((res) => {
          const newTable = {
            ...res.data,
            n_TabelaGeral: selectedTable,
            colunas: res.data.colunas.map((coluna) => ({
              title: coluna.TituloColunas,
              dataIndex: coluna.TituloColunas,
            })),
          };
          setTabelasFormulario([...tabelasFormulario, newTable]);
          setSelectedTable(null);
        })
        .catch((error) => {
          console.error("Erro ao obter colunas", error);
        });
    }
  };

  const handleRemoveTable = (tabelaIndex) => {
    const tabelaToRemove = tabelasFormulario[tabelaIndex];

    axios
      .delete(`http://localhost:8080/api/editarformulario/formularios/${selectedFormulario}/tabelas/${tabelaToRemove.n_TabelaGeral}`)
      .then((res) => {
        console.log(res);
        const newTabelasFormulario = [...tabelasFormulario];
        newTabelasFormulario.splice(tabelaIndex, 1);

        // Atualiza a lista de tabelas do formulário na UI
        setTabelasFormulario(newTabelasFormulario);

        // Mostra uma mensagem de sucesso
        alert('Tabela removida com sucesso!');
      })
      .catch((error) => {
        console.error(error);
        // Mostra uma mensagem de erro
        alert('Falha ao remover tabela. Por favor, tente novamente mais tarde.');
      });
  };

  return (
    <div>
      <Select onChange={handleFormularioChange}>
        {formularios.map((formulario) => (
          <Option key={formulario.NFormulario} value={formulario.NFormulario || ''}>{formulario.TituloFormulario}</Option>
        ))}
      </Select>
      {selectedFormulario && (
        <div>
          <Select value={selectedTable || ''} onChange={handleTableChange}>
            {tabelas.map((tabela) => (
              <Option key={tabela.n_TabelaGeral} value={tabela.n_TabelaGeral || ''}>{tabela.NomeTabelaGeral}</Option>
            ))}
          </Select>
          <Button type="primary" onClick={handleAddTable}>Adicionar tabela</Button>
        </div>
      )}
      {tabelasFormulario.map((tabela) => (
  <div key={tabela.n_TabelaGeral}>
    <Table dataSource={tabela.colunas} columns={tabela.colunas} />
    <Button type="danger" onClick={() => handleRemoveTable(tabela.n_TabelaGeral)}>Remover</Button>
  </div>
))}
    </div>
  );
}

export default RenderizarFormulario;
