import React, { useState, useEffect } from 'react';
import { Button, Form, Select } from 'antd';
import axios from 'axios';
import { Link } from "react-router-dom";

const { Option } = Select;
const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};

const tailLayout = {
  wrapperCol: {
    offset: 8,
    span: 16,
  },
};

const formulario = () => {
  const [form] = Form.useForm();
  const [maquinas, setMaquinas] = useState([]);
  const [moldes, setMoldes] = useState([]);
  const [materiaPrima, setMateriaPrima] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8080/api/listarppForm/ListMaquinas')
      .then(response => {
        setMaquinas(response.data.map(item => item.MarcaModelo));
      });

    axios.get('http://localhost:8080/api/listarppForm/ListMoldes')
      .then(response => {
        setMoldes(response.data.map(item => item.Descricao));
      });

    axios.get('http://localhost:8080/api/listarppForm/ListMP')
      .then(response => {
        setMateriaPrima(response.data.map(item => item.Descricao));
      });
  }, []);
  const onFinish = (values) => {
    console.log(values);
  };

  const onReset = () => {
    form.resetFields();
  };

  const refresh = () => window.location.reload(true);

  return (
    <Form
      {...layout}
      form={form}
      name="control-hooks"
      onFinish={onFinish}
      style={{
        maxWidth: 600,
      }}
    >
      <Form.Item
        name="maquina"
        label="Máquina"
        rules={[
          {
            required: false,
          },
        ]}
      >
        <Select placeholder="Selecione uma máquina">
          {maquinas.map(item => <Option key={item.id} value={item.id}>{item.nome}</Option>)}
        </Select>
      </Form.Item>

      <Form.Item
        name="molde"
        label="Molde"
        rules={[
          {
            required: false,
          },
        ]}
      >
        <Select placeholder="Selecione um molde">
          {moldes.map(item => <Option key={item.id} value={item.id}>{item.nome}</Option>)}
        </Select>
      </Form.Item>

      <Form.Item
        name="materiaPrima"
        label="Matéria-prima"
        rules={[
          {
            required: false,
          },
        ]}
      >
        <Select placeholder="Selecione a matéria-prima">
          {materiaPrima.map(item => <Option key={item.id} value={item.id}>{item.descricao}</Option>)}
        </Select>
      </Form.Item>

      <Form.Item {...tailLayout}>
        <Button type="primary" htmlType="submit" onClick={refresh}>
            <Link to="/pages/parametrospadrao/formulario/criarformulario">Submit</Link>
        </Button>
        <Button htmlType="button" onClick={onReset}>
          Reset
        </Button>
      </Form.Item>
    </Form>
  );
};

export default formulario;
