import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Detalhes = () => {
  const { n_ParametroPadrao } = useParams();
  const [parametroPadrao, setParametroPadrao] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/renderizarpp/parametroPadraoDetalhado/${n_ParametroPadrao}`);
        setParametroPadrao(response.data);
      } catch (error) {
        console.error("Erro ao buscar detalhes do ParametroPadrao: ", error);
      }
    };
    fetchData();
  }, [n_ParametroPadrao]); // Execute o efeito sempre que n_ParametroPadrao mudar

  // Renderizando
  if (!parametroPadrao) return <div>Carregando...</div>; // Exibir mensagem de carregamento enquanto os dados estão sendo buscados

  return (
    <div>
      Detalhes para o parametro padrao: {n_ParametroPadrao}
      <p>Descrição: {parametroPadrao.descricao}</p>
      <p>Estados:</p>
      <ul>
        {parametroPadrao.TabelaRespostas.map((resposta, index) => (
          <li key={index}>
            <p>Título da coluna: {resposta.TabelaColuna.TituloColunas}</p>
            <p>Resposta: {resposta.respostas}</p>
            <p>Tabela Geral: {resposta.TabelaColuna.TabelaGeral.designacao}</p>
          </li>
        ))}
      </ul>
      <p>n_Formulario: {parametroPadrao.n_Formularios}</p>
      <p>n_VersaoFormulario: {parametroPadrao.n_VersaoFormulario}</p>

    </div>
  );
}

export default Detalhes;
