import React, { useState } from "react";
import { Table, Button } from "antd";

const DynamicTable = () => {
  const [dataSource, setDataSource] = useState([
    {
      key: "1",
      title: "Velocidade",
      value1: "",
      value2: "",
      tolerance: "",
    },
    {
      key: "2",
      title: "Lim. Pressao",
      value1: "",
      value2: "",
      tolerance: "",
    },
  ]);
  const [columns, setColumns] = useState([
    {
      title: "Etapa1 pressao",
      dataIndex: "title",
      key: "title",
    },
    {
      title: "1º padrao",
      dataIndex: "value1",
      key: "value1",
    },
    {
      title: "2º padrao",
      dataIndex: "value2",
      key: "value2",
    },
    {
      title: "Tolerância",
      dataIndex: "tolerance",
      key: "tolerance",
      fixed: "right",
    },
  ]);

  const handleAddColumn = () => {
    setColumns((prevColumns) => [
      ...prevColumns,
      {
        title: `${prevColumns.length}º padrao`,
        dataIndex: `value${prevColumns.length}`,
        key: `value${prevColumns.length}`,
      },
    ]);
  };

  const handleDeleteColumn = (columnKey) => {
    setColumns((prevColumns) =>
      prevColumns.filter((column) => column.key !== columnKey)
    );
  };

  const handleDataChange = (key, columnKey, newValue) => {
    setDataSource((prevDataSource) =>
      prevDataSource.map((row) => {
        if (row.key === key) {
          return {
            ...row,
            [columnKey]: newValue,
          };
        }
        return row;
      })
    );
  };

  const renderDeleteColumnButton = (columnKey) => {
    return (
      <Button type="link" onClick={() => handleDeleteColumn(columnKey)}>
        Delete
      </Button>
    );
  };

  return (
    <>
      <Button type="primary" onClick={handleAddColumn}>
        Add Column
      </Button>
      <Table
        dataSource={dataSource}
        columns={columns.map((column) => ({
          ...column,
          render: (_, row) => {
            const columnKey = column.key;
            if (columnKey === "title") {
              return row[columnKey];
            }
            return (
              <>
                <input
                  value={row[columnKey]}
                  onChange={(e) =>
                    handleDataChange(row.key, columnKey, e.target.value)
                  }
                />
                {row.key === "1" && (
                  <input
                    value={row.tolerance}
                    onChange={(e) =>
                      handleDataChange(row.key, "tolerance", e.target.value)
                    }
                  />
                )}
              </>
            );
          },
          title: (
            <>
              {column.title}{" "}
              {column.dataIndex !== "title" &&
                renderDeleteColumnButton(column.key)}
            </>
          ),
        }))}
        pagination={false}
        rowKey="key"
        scroll={{ x: true }}
      />
    </>
  );
};

export default DynamicTable;
