import React, { useState } from "react";
import { Table, Button } from "antd";

const DynamicTable = () => {
  const [dataSource, setDataSource] = useState([
    { key: "1", name: "", value1: "", value2: "", tolerance: "" },
    { key: "2", name: "", value1: "", value2: "", tolerance: "" },
    { key: "3", name: "tolerancia", value1: "", value2: "", tolerance: "" },
  ]);
  const [columns, setColumns] = useState([
    {
      title: "etapa1 pressao",
      key: "etapa1",
      children: [
        {
          title: "1º padrao",
          dataIndex: "value1",
          key: "value1",
          width: 100,
          editable: true,
        },
        {
          title: "2º padrao",
          dataIndex: "value2",
          key: "value2",
          width: 100,
          editable: true,
        },
      ],
    },
    {
      title: "tolerancia",
      dataIndex: "tolerance",
      key: "tolerance",
      width: 100,
      fixed: "right",
    },
  ]);

  const handleAddRow = () => {
    setDataSource((prevDataSource) => [
      ...prevDataSource,
      {
        key: (prevDataSource.length + 1).toString(),
        name: "",
        value1: "",
        value2: "",
        tolerance: "",
      },
    ]);
  };

  const handleAddColumn = () => {
    const columnNumber = columns.length - 1; // subtracting 1 to ignore the tolerance column
    setColumns((prevColumns) => [
      ...prevColumns.slice(0, -1),
      {
        title: `${columnNumber + 1}º padrao`,
        key: `${columnNumber + 1}`,
        children: [
          {
            title: "Velocidade",
            dataIndex: `value${columnNumber + 1}_1`,
            key: `value${columnNumber + 1}_1`,
            width: 100,
            editable: true,
          },
          {
            title: "Lim. Pressao",
            dataIndex: `value${columnNumber + 1}_2`,
            key: `value${columnNumber + 1}_2`,
            width: 100,
            editable: true,
          },
        ],
      },
      prevColumns[prevColumns.length - 1],
    ]);
  };

  const handleDeleteColumn = (columnKey) => {
    if (columnKey === "tolerance") return; // prevent deletion of tolerance column
    setColumns((prevColumns) =>
      prevColumns.filter((column) => column.key !== columnKey)
    );
    setDataSource((prevDataSource) =>
      prevDataSource.map((row) => {
        const newRow = { ...row };
        delete newRow[columnKey];
        return newRow;
      })
    );
  };

  const handleDataChange = (rowKey, columnKey, value) => {
    setDataSource((prevDataSource) =>
      prevDataSource.map((row) => {
        if (row.key === rowKey) {
          return {
            ...row,
            [columnKey]: value,
          };
        }
        return row;
      })
    );
  };

  const renderDeleteColumnButton = (columnKey) => {
    if (columnKey === "tolerance") return null; // hide delete button for tolerance column
    return (
      <Button onClick={() => handleDeleteColumn(columnKey)}>Delete</Button>
    );
  };

  const components = {
    body: {
      cell: EditableCell,
    },
  };

  const columnsWithDeleteButton = columns.map((column) => {
    return {
      ...column,
      render: (text, record) => {
        const { key } = column;
        return (
          <div>
            <EditableCell
              key={key}
              rowKey={record.key}
              columnKey={key}
              value={record[key]}
              onChange={handleDataChange}
              editable={column.editable}
            />
            {renderDeleteColumnButton(key)}
          </div>
        );
      },
    };
  });

  return (
    <div>
      <Button onClick={handleAddRow}>Add Row</Button>
      <Button onClick={handleAddColumn}>Add Column</Button>
      <Table
        dataSource={dataSource}
        columns={columnsWithDeleteButton}
        components={components}
        bordered
        pagination={false}
        scroll={{ x: "max-content" }}
      />
    </div>
  );
};

const EditableCell = ({ rowKey, columnKey, value, onChange, editable }) => {
  const [editing, setEditing] = useState(false);
  const handleCellClick = () => {
    if (editable) {
      setEditing(true);
    }
  };
  const handleCellBlur = () => {
    if (editing) {
      setEditing(false);
    }
  };
  const handleInputChange = (e) => {
    const newValue = e.target.value;
    onChange(rowKey, columnKey, newValue);
  };
  return (
    <div
      onClick={handleCellClick}
      onBlur={handleCellBlur}
      contentEditable={editing}
      suppressContentEditableWarning={!editable}
      style={{
        minWidth: "100px",
        minHeight: "20px",
        display: "inline-block",
        backgroundColor: editing ? "#fafafa" : "transparent",
      }}
    >
      {value}
      {editable && (
        <input
          type="text"
          value={value}
          onChange={handleInputChange}
          style={{
            width: "100%",
            height: "100%",
            border: "none",
            padding: "0px 5px",
            fontSize: "14px",
          }}
        />
      )}
    </div>
  );
};

export default DynamicTable;
