import React, { useState, useEffect } from "react";
import axios from "axios";
import { Select, Table, Button } from "antd";

const { Option } = Select;

function RenderizarFormulario() {
  const [formularios, setFormularios] = useState([]);
  const [tabelas, setTabelas] = useState([]);
  const [selectedFormulario, setSelectedFormulario] = useState(null);
  const [tabelasFormulario, setTabelasFormulario] = useState([]);
  const [selectedTable, setSelectedTable] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/editarformulario/formularios")
      .then((res) => {
        console.log('Resposta da API (Formulários):', res.data);
        setFormularios(res.data);
      })
      .catch((error) => {
        console.error("Erro ao obter formulários", error);
      });
      
    axios
      .get("http://localhost:8080/api/escolhertabela/tabelas-geral")
      .then((res) => {
        setTabelas(res.data);
      })
      .catch((error) => {
        console.error("Erro ao obter tabelas", error);
      });

  }, []);

  const handleFormularioChange = (value) => {
    setSelectedFormulario(value);
    axios
      .get(`http://localhost:8080/api/editarformulario/formularios/${value}`)
      .then((res) => {
        console.log('Resposta da API (Tabelas do Formulário):', res.data);
        setTabelasFormulario(res.data);
      })
      .catch((error) => {
        console.error("Erro ao obter tabelas do formulário", error);
      });
  };

  const handleTableChange = (value) => {
    setSelectedTable(value);
  };

  const handleAddTable = () => {
    if(selectedTable){
      axios
        .get(`http://localhost:8080/api/escolhertabela/colunas/${selectedTable}`)
        .then((res) => {
          const newTable = {
            ...res.data,
            id: selectedTable,
            colunas: res.data.colunas.map((coluna) => ({
              title: coluna.TituloColunas,
              dataIndex: coluna.TituloColunas,
            })),
          };
          // atualiza tabelasFormulario em vez de tabelas
          setTabelasFormulario([...tabelasFormulario, newTable]);
          setSelectedTable(null);
        })
        .catch((error) => {
          console.error("Erro ao obter colunas", error);
        });
    }
  };

  

//   axios
//   .post(`http://localhost:8080/api/editarformulario/formularios/${selectedFormulario}/adicionar`, {
//     tabelas: tabelasFormulario.map((table) => table.id),
//   })
//   .then((res) => {
//     console.log(res);
//   })
//   .catch((error) => {
//     console.error("Erro ao guardar", error);
//   });

const handleGuardar = () => {
    axios
      .post(`http://localhost:8080/api/editarformulario/formularios/${selectedFormulario}/adicionar`, {
        // passa os IDs das tabelas em tabelasFormulario em vez de tabelas
        tabelas: tabelasFormulario.map((table) => table.id),
      })
      .then((res) => {
        console.log(res);
      })
      .catch((error) => {
        console.error("Erro ao guardar", error);
      });
  };




  return (
    <div>
      <h1>Escolha um Formulário</h1>
      <Select
        style={{ width: 200 }}
        onChange={handleFormularioChange}
      >
        {formularios.map((formulario) => (
          <Option key={formulario.n_Formularios} value={formulario.n_Formularios}>
            {formulario.designacao}
          </Option>
        ))}
      </Select>
      {tabelasFormulario.map((tabela, index) => {
        console.log('Renderizando tabela:', tabela);
        return (
          <div key={index} style={{ marginBottom: "20px" }}>
            <Table
              columns={tabela.colunas}
              dataSource={Array.from({ length: tabela.numero_linhas }, (_, i) => ({
                key: i,
              }))}
              pagination={false}
            />
          </div>
        );
      })}
      <Select
        style={{ width: 200 }}
        onChange={handleTableChange}
      >
        {tabelas.map((tabela) => (
          <Option key={tabela.n_TabelaGeral} value={tabela.n_TabelaGeral}>
            {tabela.designacao}
          </Option>
        ))}
      </Select>
      <Button onClick={handleAddTable}>Adicionar Tabela</Button>
      <Button onClick={handleGuardar}>Guardar</Button>
    </div>
  );
}

export default RenderizarFormulario;
