import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Table } from 'antd';

const Detalhes = () => {
  const { n_ParametroPadrao } = useParams();
  const [parametroPadrao, setParametroPadrao] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/renderizarpp/parametroPadraoDetalhado/${n_ParametroPadrao}`);
        setParametroPadrao(response.data);
        console.log(response.data);
      } catch (error) {
        console.error("Erro ao buscar detalhes do ParametroPadrao: ", error);
      }
    };
    fetchData();
  }, [n_ParametroPadrao]);

  let tabelas = {};
  let linhaAtual = {};
  if (parametroPadrao) {
    for (const resposta of parametroPadrao.TabelaRespostas) {
      const idTabela = resposta.TabelaColuna.n_TabelaGeral;
      if (!tabelas[idTabela]) {
        tabelas[idTabela] = {
          designacao: resposta.TabelaColuna.TabelaGeral.designacao,
          numero_linhas: resposta.TabelaColuna.TabelaGeral.numero_linhas,
          colunas: new Set(),
          linhas: [],
        };
        // Inicializa a linha atual para cada tabela
        linhaAtual[idTabela] = 0;
      }
      tabelas[idTabela].colunas.add(resposta.TabelaColuna.TituloColunas);
  
      // Usa a linha atual em vez de calcular a linha baseada no número da resposta
      const n_Linha = linhaAtual[idTabela] + 1;
  
      if (!tabelas[idTabela].linhas[n_Linha - 1]) {
        tabelas[idTabela].linhas[n_Linha - 1] = { key: n_Linha };
      }
  
      tabelas[idTabela].linhas[n_Linha - 1][resposta.TabelaColuna.TituloColunas] = resposta.respostas;
  
      // Incrementa a linha atual após uma resposta ser adicionada
      linhaAtual[idTabela]++;
    }
  }
  

  console.log(tabelas)

  return (
    <div>
      {parametroPadrao ? (
        <>
          Detalhes para o parametro padrao: {n_ParametroPadrao}
          <p>Descrição: {parametroPadrao.descricao}</p>
          <p>Estados:</p>
          {Object.entries(tabelas).map(([key, tabela]) => (
            <div key={key}>
              <h2>{tabela.designacao}</h2>
              <Table
                columns={Array.from(tabela.colunas).map((coluna) => ({
                  title: coluna,
                  dataIndex: coluna,
                  key: coluna,
                }))}
                dataSource={tabela.linhas}
                pagination={false}
              />
              {              console.log(tabela.linhas)}
            </div>
          ))}
          <p>n_Formulario: {parametroPadrao.n_Formularios}</p>
          <p>n_VersaoFormulario: {parametroPadrao.n_VersaoFormulario}</p>
        </>
      ) : (
        <p>A carregar...</p>
      )}
    </div>
  );
};

export default Detalhes;
